$gitScript = "https://raw.githubusercontent.com/ronnm-veeam/cisco-intersight/main/BR-IntersightCredentialCheck.ps1"

$pathElements = "$gitScript" -split '/'
$scriptName = $pathElements[$PathElements.Length-1]

Invoke-WebRequest -Uri $gitScript -OutFile "$env:TEMP\$scriptName"

$script = "$env:TEMP\$scriptName"
& $script {{.global.task.input.username}} {{.global.task.input.userpassword}} "{{.global.task.input.userdescription}}"
